import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import Replies from "./Replies";

function PostThread() {
  const { postId } = useParams(); 
  const [post, setPost] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchPost = async () => {
      try {
        const response = await fetch(`/thread/${postId}`);
        const data = await response.json();
        setPost(data);
      } catch (error) {
        console.error("Error fetching post:", error);
        setPost(null);
      } finally {
        setLoading(false);
      }
    };

    fetchPost();
  }, [postId]);

  return (
    <div style={styles.container}>
      {loading ? (
        <p>Loading post...</p>
      ) : post ? (
        <>
          <h2 style={styles.title}>{post.post.type}</h2>
          <p style={styles.message}>{post.post.message}</p>
          <span style={styles.user}>- {post.post.user}</span>
          <Replies postId={post.post._id} /> {/* Display replies */}
          <Link to="/chat/" style={styles.backLink}>
            &larr; Back to Chat
          </Link>
        </>
      ) : (
        <p>Post not found.</p>
      )}
    </div>
  );
}

const styles = {
  container: {
    padding: "40px 20px",
    maxWidth: "800px",
    margin: "0 auto",
    fontFamily: "'Arial', sans-serif",
    color: "#333",
    lineHeight: 1.6,
  },
  title: {
    fontSize: "2rem",
    fontWeight: "bold",
    marginBottom: "20px",
  },
  message: {
    fontSize: "1rem",
    marginBottom: "20px",
  },
  user: {
    fontSize: "1rem",
    color: "#555",
  },
  backLink: {
    display: "block",
    textDecoration: "none",
    color: "#007bff",
    fontSize: "1rem",
    marginTop: "20px",
    transition: "color 0.3s ease",
  },
};

export default PostThread;
