import React, { useState } from "react";
import { useParams, Link } from "react-router-dom";
import PostForm from "./PostForm";

function ChatPage() {
  const { raceId } = useParams();
  const [posts, setPosts] = useState([
    { id: 1, type: "Selling", message: "Selling bib for Boston Marathon", user: "Alice" },
    { id: 2, type: "Buying", message: "Looking for a bib for Chicago Marathon", user: "Bob" },
  ]);

  const handleNewPost = (newPost) => {
    setPosts([...posts, newPost]);
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Chat for Race ID: {raceId}</h2>
      <Link to="/">Back to Home</Link>
      <div style={{ marginTop: "20px" }}>
        {posts
          .filter((post) => post.raceId === Number(raceId) || post.raceId === undefined)
          .map((post) => (
            <div key={post.id} style={{ border: "1px solid #ccc", padding: "10px", marginBottom: "10px" }}>
              <strong>{post.type}</strong>: {post.message} <em>- {post.user}</em>
            </div>
          ))}
      </div>
      <PostForm raceId={Number(raceId)} onNewPost={handleNewPost} />
    </div>
  );
}

export default ChatPage;
