import React, { useState, useEffect, useMemo } from "react";
import { useNavigate } from "react-router-dom";

function WelcomePage() {
  const navigate = useNavigate();
  const [typedText, setTypedText] = useState("");
  const [isDeleting, setIsDeleting] = useState(false);
  const [loopIndex, setLoopIndex] = useState(0);
  const [isTypingComplete, setIsTypingComplete] = useState(false);
  const [isSecondMessageComplete, setIsSecondMessageComplete] = useState(false);
  const [allMessagesComplete, setAllMessagesComplete] = useState(false);

  const messages = useMemo(() => ["I want to sell my bib", "I want to buy a bib", "Transfer My Bib :)"], []);

  const typingSpeed = 110; 
  const deletingSpeed = 50; 
  const delayBetweenMessages = 1000;

  useEffect(() => {
    if (isTypingComplete && allMessagesComplete ) return;

    const fullMessage = messages[loopIndex % messages.length];
    const baseText = "I want"; // Common text to preserve during deletion
    const isTyping = !isDeleting;

    const timeout = setTimeout(() => {
      if (isTyping && !isSecondMessageComplete) {
        // Typing logic
        setTypedText((prev) => fullMessage.slice(0, prev.length + 1));
        if (typedText === fullMessage) {
          setTimeout(() => setIsDeleting(true), delayBetweenMessages);
        }
      } 
      else if(isDeleting && !isSecondMessageComplete){

        setTypedText((prev) =>
          prev.length > baseText.length ? prev.slice(0, prev.length - 1) : prev
        );
        if (typedText === baseText) {
          setIsDeleting(false);
          setLoopIndex((prev) => prev + 1);
        }
      }
      else if(isTyping && !allMessagesComplete){

      }

      if (loopIndex === messages.length - 2 && typedText === messages[messages.length -2]) {
        setIsTypingComplete(true);
        setIsSecondMessageComplete(true);
        setLoopIndex((prev) => prev + 1)
      }
      elif
    }, isTyping ? typingSpeed : deletingSpeed);

    return () => clearTimeout(timeout);
  }, [typedText, isDeleting, loopIndex, messages, isTypingComplete]);

  const styles = {
    container: {
      display: "flex",
      flexDirection: "column",
      justifyContent: "center",
      alignItems: "center",
      height: "100vh",
      backgroundColor: "#f4f4f4",
      fontFamily: "Arial, sans-serif",
    },
    heading: {
      fontSize: "2.5rem",
      fontWeight: "bold",
      textAlign: "center",
      margin: "0 0 20px",
      color: "#333",
    },
    button: {
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      marginTop: "20px",
      padding: "10px 20px",
      fontSize: "1rem",
      color: "#fff",
      backgroundColor: "#007bff",
      border: "none",
      borderRadius: "5px",
      cursor: "pointer",
      textDecoration: "none",
      transition: "background-color 0.3s ease",
    },
    buttonHover: {
      backgroundColor: "#0056b3",
    },
    arrow: {
      marginLeft: "10px",
      display: "inline-block",
    },
    typingProgress: {
      fontSize: "1rem",
      color: "#666",
    },
  };

  return (
    <div style={styles.container}>
      <h1 style={styles.heading}>{typedText}</h1>
      {!isTypingComplete && <p style={styles.typingProgress}></p>}
      {isSecondMessageComplete && (
        <button
          style={styles.button}
          onMouseOver={(e) => (e.target.style.backgroundColor = styles.buttonHover.backgroundColor)}
          onMouseOut={(e) => (e.target.style.backgroundColor = styles.button.backgroundColor)}
          onClick={() => navigate("/home")}
        >
          Get Started <span style={styles.arrow}>â†’</span>
        </button>
      )}
    </div>
  );
}

export default WelcomePage;
