import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import PostForm from "./PostForm";  // You might need to modify this file or handle the form here.

function ChatPage() {
  const { raceId } = useParams(); // Get the raceId from the URL
  const [posts, setPosts] = useState([]);  // Initially, set posts to an empty array
  const [loading, setLoading] = useState(true);  // Track loading state
  const [name, setName] = useState("");  // State for the user's name
  const [message, setMessage] = useState("");  // State for the post message

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch(`http://localhost:8080/chat/${raceId}`);
        const data = await response.json();
        console.log('Fetched data:', data);  // Log the fetched data

        if (Array.isArray(data)) {
          setPosts(data); 
        } else {
          console.error("Data is not an array:", data);
          setPosts([]);  // If data is not an array, set posts to an empty array
        }
      } catch (error) {
        console.error("Error fetching posts:", error);
        setPosts([]);  // Set posts to empty array if error occurs
      } finally {
        setLoading(false);
      }
    };

    fetchPosts();  
  }, [raceId]);  

  const handleNewPost = () => {
    if (name && message) {
      const newPost = {
        type: "Message",  // You can adjust the type based on the post form
        message,
        user: name,
      };
      setPosts([...posts, newPost]);  // Add the new post to the posts array
      setMessage("");  // Clear the message input
      setName("");  // Clear the name input
    } else {
      alert("Please enter both your name and a message.");
    }
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>{raceId} Marathon Chat</h2>
      <Link to="/" style={styles.backLink}>
        &larr; Back to Home
      </Link>

      {/* User input for name and message */}
      <div style={styles.inputContainer}>
        <input 
          type="text" 
          placeholder="Enter your name" 
          value={name}
          onChange={(e) => setName(e.target.value)}
          style={styles.input}
        />
        <textarea 
          placeholder="Enter your message..." 
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          style={styles.textarea}
        />
        <button onClick={handleNewPost} style={styles.submitButton}>Post</button>
      </div>

      <div style={styles.postsContainer}>
        {loading ? (
          <p>Loading posts...</p>  // Show loading message while fetching data
        ) : posts.length === 0 ? (
          <div style={styles.noPostsMessage}>No activity here yet!</div> // Show "No activity" message when there are no posts
        ) : (
          posts.map((post) => (
            <div key={post._id} style={styles.postCard}>  {/* Use _id from MongoDB */}
              <strong style={styles.postType}>{post.type}</strong>
              <p style={styles.postMessage}>{post.message}</p>
              <span style={styles.postUser}>- {post.user}</span>
            </div>
          ))
        )}
      </div>
    </div>
  );
}

const styles = {
  container: {
    padding: "40px 20px",
    maxWidth: "800px",
    margin: "0 auto",
    fontFamily: "'Arial', sans-serif",
    color: "#333",
    lineHeight: 1.6,
  },
  title: {
    fontSize: "2rem",
    fontWeight: "bold",
    marginBottom: "20px",
    textAlign: "center",
  },
  backLink: {
    display: "block",
    textDecoration: "none",
    color: "#007bff",
    fontSize: "1rem",
    marginBottom: "30px",
    transition: "color 0.3s ease",
    textAlign: "center",
  },
  postsContainer: {
    marginTop: "20px",
    marginBottom: "40px",
    display: "flex",
    flexDirection: "column",
    gap: "15px",
  },
  postCard: {
    backgroundColor: "#f8f9fa",
    border: "1px solid #ddd",
    borderRadius: "8px",
    padding: "15px",
    transition: "box-shadow 0.3s ease",
  },
  postType: {
    fontSize: "1.1rem",
    fontWeight: "bold",
    color: "#333",
  },
  postMessage: {
    fontSize: "1rem",
    margin: "10px 0",
  },
  postUser: {
    fontSize: "0.9rem",
    color: "#666",
  },
  noPostsMessage: {
    fontSize: "1.2rem",
    fontWeight: "bold",
    color: "#ff9900",  // Yellowish color to stand out
    textAlign: "center",
    padding: "20px",
    backgroundColor: "#f8f9fa",  // Light background to make the message stand out
    border: "1px solid #ddd",
    borderRadius: "8px",
  },
  inputContainer: {
    marginBottom: "20px",
    display: "flex",
    flexDirection: "column",
    gap: "10px",
    alignItems: "center",
  },
  input: {
    padding: "10px",
    width: "80%",
    border: "1px solid #ddd",
    borderRadius: "8px",
    fontSize: "1rem",
  },
  textarea: {
    padding: "10px",
    width: "80%",
    height: "100px",
    border: "1px solid #ddd",
    borderRadius: "8px",
    fontSize: "1rem",
    resize: "none",
  },
  submitButton: {
    padding: "10px 20px",
    fontSize: "1rem",
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    borderRadius: "8px",
    cursor: "pointer",
    transition: "background-color 0.3s ease",
  },
};

export default ChatPage;
