import React, { useState } from "react";
import { useParams, Link } from "react-router-dom";
import PostForm from "./PostForm";

function ChatPage() {
  const { raceName } = useParams();
  const [posts, setPosts] = useState([
    { id: 1, type: "Selling", message: "Selling bib for Boston Marathon", user: "Alice" },
    { id: 2, type: "Buying", message: "Looking for a bib for Chicago Marathon", user: "Bob" },
  ]);

  const handleNewPost = (newPost) => {
    setPosts([...posts, newPost]);
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>Chat for Race ID: {raceId}</h2>
      <Link to="/" style={styles.backLink}>
        &larr; Back to Home
      </Link>
      <div style={styles.postsContainer}>
        {posts
          .filter((post) => post.raceName === Number(raceName) || post.raceName === undefined)
          .map((post) => (
            <div key={post.id} style={styles.postCard}>
              <strong style={styles.postType}>{post.type}</strong>
              <p style={styles.postMessage}>{post.message}</p>
              <span style={styles.postUser}>- {post.user}</span>
            </div>
          ))}
      </div>
      <PostForm raceId={Number(raceId)} onNewPost={handleNewPost} />
    </div>
  );
}

const styles = {
  container: {
    padding: "40px 20px",
    maxWidth: "800px",
    margin: "0 auto",
    fontFamily: "'Arial', sans-serif",
    color: "#333",
    lineHeight: 1.6,
  },
  title: {
    fontSize: "2rem",
    fontWeight: "bold",
    marginBottom: "20px",
    textAlign: "center",
  },
  backLink: {
    display: "block",
    textDecoration: "none",
    color: "#007bff",
    fontSize: "1rem",
    marginBottom: "30px",
    transition: "color 0.3s ease",
    textAlign: "center",
  },
  postsContainer: {
    marginTop: "20px",
    marginBottom: "40px",
    display: "flex",
    flexDirection: "column",
    gap: "15px",
  },
  postCard: {
    backgroundColor: "#f8f9fa",
    border: "1px solid #ddd",
    borderRadius: "8px",
    padding: "15px",
    transition: "box-shadow 0.3s ease",
  },
  postType: {
    fontSize: "1.1rem",
    fontWeight: "bold",
    color: "#333",
  },
  postMessage: {
    fontSize: "1rem",
    margin: "10px 0",
  },
  postUser: {
    fontSize: "0.9rem",
    color: "#666",
  },
};

export default ChatPage;
