import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import PostForm from "./PostForm";

function ChatPage() {
  const { raceId } = useParams();
  const [posts, setPosts] = useState([]);
  const [replies, setReplies] = useState({});
  const [loading, setLoading] = useState(true);
  const [raceName, setRaceName] = useState("");

  useEffect(() => {
    const fetchRaceName = async () => {
      try {
        const response = await fetch(`/races/${raceId}`);
        const data = await response.json();
        setRaceName(data.name || "Unknown Race");
      } catch (error) {
        console.error("Error fetching race name:", error);
        setRaceName("Unknown Race");
      }
    };

    const fetchPosts = async () => {
      try {
        const response = await fetch(`/chat/${raceId}`);
        const data = await response.json();
        setPosts(data || []);
      } catch (error) {
        console.error("Error fetching posts:", error);
        setPosts([]);
      } finally {
        setLoading(false);
      }
    };

    fetchRaceName();
    fetchPosts();
  }, [raceId]);

  const fetchReplies = async (postId) => {
    try {
      const response = await fetch(`/replies/${postId}`);
      const data = await response.json();
      setReplies((prevReplies) => ({
        ...prevReplies,
        [postId]: data || [],
      }));
    } catch (error) {
      console.error(`Error fetching replies for post ${postId}:`, error);
      setReplies((prevReplies) => ({
        ...prevReplies,
        [postId]: [],
      }));
    }
  };

  const handleNewReply = (postId, newReply) => {
    setReplies((prevReplies) => ({
      ...prevReplies,
      [postId]: [...(prevReplies[postId] || []), newReply],
    }));
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>{raceName} Chat</h2>
      <Link to="/" style={styles.backLink}>
        &larr; Back to Home
      </Link>
      <div style={styles.postsContainer}>
        {loading ? (
          <p>Loading posts...</p>
        ) : posts.length === 0 ? (
          <div style={styles.noPostsMessage}>No activity here yet!</div>
        ) : (
          posts.map((post) => (
            <div key={post._id} style={styles.postCard}>
              <strong style={styles.postType}>{post.type}</strong>
              <p style={styles.postMessage}>{post.message}</p>
              <span style={styles.postUser}>- {post.user}</span>
              {/* Reply section */}
              <button
                style={styles.replyButton}
                onClick={() => fetchReplies(post._id)}
              >
                View Replies
              </button>
              <div style={styles.repliesContainer}>
                {(replies[post._id] || []).map((reply) => (
                  <div key={reply._id} style={styles.replyCard}>
                    <p style={styles.replyMessage}>{reply.message}</p>
                    <span style={styles.replyUser}>- {reply.user}</span>
                  </div>
                ))}
              </div>
              <PostForm
                raceId={post._id} // Pass post ID for replies
                onNewPost={(newReply) => handleNewReply(post._id, newReply)}
                placeholder="Write a reply..."
              />
            </div>
          ))
        )}
      </div>
      <PostForm raceId={Number(raceId)} onNewPost={(newPost) => setPosts([...posts, newPost])} />
    </div>
  );
}

const styles = {
  container: {
    padding: "40px 20px",
    maxWidth: "800px",
    margin: "0 auto",
    fontFamily: "'Arial', sans-serif",
    color: "#333",
    lineHeight: 1.6,
  },
  title: {
    fontSize: "2rem",
    fontWeight: "bold",
    marginBottom: "20px",
    textAlign: "center",
  },
  backLink: {
    display: "block",
    textDecoration: "none",
    color: "#007bff",
    fontSize: "1rem",
    marginBottom: "30px",
    transition: "color 0.3s ease",
    textAlign: "center",
  },
  postsContainer: {
    marginTop: "20px",
    marginBottom: "40px",
    display: "flex",
    flexDirection: "column",
    gap: "15px",
  },
  postCard: {
    backgroundColor: "#f8f9fa",
    border: "1px solid #ddd",
    borderRadius: "8px",
    padding: "15px",
    transition: "box-shadow 0.3s ease",
  },
  postType: {
    fontSize: "1.1rem",
    fontWeight: "bold",
    color: "#333",
  },
  postMessage: {
    fontSize: "1rem",
    margin: "10px 0",
  },
  postUser: {
    fontSize: "0.9rem",
    color: "#666",
  },
  replyButton: {
    marginTop: "10px",
    background: "#007bff",
    color: "#fff",
    border: "none",
    padding: "5px 10px",
    borderRadius: "5px",
    cursor: "pointer",
  },
  repliesContainer: {
    marginTop: "10px",
    marginLeft: "20px",
    display: "flex",
    flexDirection: "column",
    gap: "10px",
  },
  replyCard: {
    backgroundColor: "#e9ecef",
    border: "1px solid #ddd",
    borderRadius: "5px",
    padding: "10px",
  },
  replyMessage: {
    margin: "0 0 5px",
  },
  replyUser: {
    fontSize: "0.9rem",
    color: "#555",
  },
};

export default ChatPage;
