import React from "react";
import { Link } from "react-router-dom";

const races = [
  { id: 1, name: "Boston Marathon" },
  { id: 2, name: "Chicago Marathon" },
  { id: 3, name: "New York Marathon" },
];

function HomePage() {
  return (
    <div style={styles.container}>
      <h1 style={styles.title}>Bib Transfer Portal</h1>
      <p style={styles.subtitle}>Find or transfer bibs for popular races below:</p>
      <ul style={styles.raceList}>
        {races.map((race) => (
          <li key={race.id} style={styles.raceItem}>
            <Link to={`/chat/${race.id}`} style={styles.raceLink}>
              {race.name}
            </Link>
          </li>
        ))}
      </ul>
    </div>
  );
}

const styles = {
  container: {
    padding: "40px 20px",
    maxWidth: "800px",
    margin: "0 auto",
    textAlign: "center",
    fontFamily: "'Arial', sans-serif",
    color: "#333",
    lineHeight: 1.6,
  },
  title: {
    fontSize: "2.5rem",
    fontWeight: "bold",
    marginBottom: "10px",
  },
  subtitle: {
    fontSize: "1.2rem",
    marginBottom: "30px",
    color: "#666",
  },
  raceList: {
    listStyleType: "none",
    padding: 0,
    display: "flex",
    flexDirection: "column",
    gap: "15px",
  },
  raceItem: {
    backgroundColor: "#f8f9fa",
    border: "1px solid #ddd",
    borderRadius: "8px",
    transition: "box-shadow 0.3s ease",
  },
  raceLink: {
    display: "block",
    textDecoration: "none",
    color: "#007bff",
    fontSize: "1.2rem",
    fontWeight: "500",
    padding: "15px 20px",
    borderRadius: "8px",
    transition: "background-color 0.3s ease, color 0.3s ease",
  },
  raceItemHover: {
    boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
  },
  raceLinkHover: {
    backgroundColor: "#007bff",
    color: "#fff",
  },
};

export default HomePage;
