import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import PostForm from "./PostForm";

function ChatPage() {
  const { raceId } = useParams(); // Get the raceId from the URL
  const [posts, setPosts] = useState([]);  // Initially, set posts to an empty array
  const [loading, setLoading] = useState(true);  // Track loading state

  // Fetch posts for the specific race when the component is mounted
  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch(`http://localhost:8080/posts/${raceId}`);
        const data = await response.json();
        setPosts(data);  // Update the posts state with the fetched data
      } catch (error) {
        console.error("Error fetching posts:", error);
      } finally {
        setLoading(false);  // Set loading to false once the fetch is complete
      }
    };

    fetchPosts();  // Call the fetchPosts function
  }, [raceId]);  // This effect will run whenever the raceId changes

  const handleNewPost = (newPost) => {
    setPosts([...posts, newPost]);  // Add the new post to the posts array
  };

  return (
    <div style={styles.container}>
      <h2 style={styles.title}>{raceId} Marathon Chat</h2>
      <Link to="/" style={styles.backLink}>
        &larr; Back to Home
      </Link>
      <div style={styles.postsContainer}>
        {loading ? (
          <p>Loading posts...</p>  // Show loading message while fetching data
        ) : (
          posts.map((post) => (
            <div key={post._id} style={styles.postCard}>  {/* Use _id from MongoDB */}
              <strong style={styles.postType}>{post.type}</strong>
              <p style={styles.postMessage}>{post.message}</p>
              <span style={styles.postUser}>- {post.user}</span>
            </div>
          ))
        )}
      </div>
      <PostForm raceId={Number(raceId)} onNewPost={handleNewPost} />
    </div>
  );
}

const styles = {
  container: {
    padding: "40px 20px",
    maxWidth: "800px",
    margin: "0 auto",
    fontFamily: "'Arial', sans-serif",
    color: "#333",
    lineHeight: 1.6,
  },
  title: {
    fontSize: "2rem",
    fontWeight: "bold",
    marginBottom: "20px",
    textAlign: "center",
  },
  backLink: {
    display: "block",
    textDecoration: "none",
    color: "#007bff",
    fontSize: "1rem",
    marginBottom: "30px",
    transition: "color 0.3s ease",
    textAlign: "center",
  },
  postsContainer: {
    marginTop: "20px",
    marginBottom: "40px",
    display: "flex",
    flexDirection: "column",
    gap: "15px",
  },
  postCard: {
    backgroundColor: "#f8f9fa",
    border: "1px solid #ddd",
    borderRadius: "8px",
    padding: "15px",
    transition: "box-shadow 0.3s ease",
  },
  postType: {
    fontSize: "1.1rem",
    fontWeight: "bold",
    color: "#333",
  },
  postMessage: {
    fontSize: "1rem",
    margin: "10px 0",
  },
  postUser: {
    fontSize: "0.9rem",
    color: "#666",
  },
};

export default ChatPage;
