import React, { useState, useEffect, useMemo } from "react";
import { useNavigate } from "react-router-dom";

function WelcomePage() {
  const navigate = useNavigate();
  const [typedText, setTypedText] = useState("");
  const [isDeleting, setIsDeleting] = useState(false);
  const [loopIndex, setLoopIndex] = useState(0);
  const [isTypingComplete, setIsTypingComplete] = useState(false);

  const messages = useMemo(() => ["I want to sell my bib", "I want to buy a bib"], []);

  const typingSpeed = 100; // Speed of typing in ms
  const deletingSpeed = 50; // Speed of deleting in ms
  const delayBetweenMessages = 1000; // Delay before switching between messages

  useEffect(() => {

    if (isTypingComplete) return;

    const currentMessage = messages[loopIndex % messages.length];
    const isTyping = !isDeleting;

    const timeout = setTimeout(() => {
      if (isTyping) {
        // Typing logic
        setTypedText((prev) => currentMessage.slice(0, prev.length + 1));
        if (typedText === currentMessage) {
          setTimeout(() => setIsDeleting(true), delayBetweenMessages);
        }
      } else {
        // Deleting logic
        setTypedText((prev) => prev.slice(0, prev.length - 1));
        if (typedText === "") {
          setIsDeleting(false);
          setLoopIndex((prev) => prev + 1);
        }
      }

      if (loopIndex === messages.length - 1 && typedText === "") {
        setIsTypingComplete(true);
      }
    }, isTyping ? typingSpeed : deletingSpeed);

    return () => clearTimeout(timeout);
  }, [typedText, isDeleting, loopIndex, messages, isTypingComplete]);

  return (
    <div style={styles.container}>
      <div style={styles.textContainer}>
        <h1 style={styles.typingText}>{typedText}</h1>
        {!isTypingComplete && <span style={styles.cursor}>|</span>}
      </div>
      {isTypingComplete && (
        <button
          style={styles.button}
          onClick={() => navigate("/home")}
        >
          Get Started <span style={styles.arrow}><img src="/arrow.png" alt="favicon" style={styles.arrow} /></span>
        </button>
      )}
    </div>
  );
}

const styles = {
  container: {
    height: "100vh",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    backgroundColor: "#f5f5f5",
    fontFamily: "'Roboto', sans-serif",
    textAlign: "center",
  },
  textContainer: {
    fontSize: "2rem",
    fontWeight: "bold",
    color: "#333",
    marginBottom: "30px",
    position: "relative",
  },
  typingText: {
    display: "inline-block",
  },
  cursor: {
    display: "inline-block",
    marginLeft: "5px",
    width: "3px",
    backgroundColor: "#333",
    animation: "blink 0.8s step-end infinite",
  },
  button: {
    backgroundColor: "#007bff",
    color: "#fff",
    border: "none",
    padding: "15px 30px",
    borderRadius: "5px",
    fontSize: "1.2rem",
    cursor: "pointer",
    display: "flex",
    alignItems: "center",
    gap: "10px",
    boxShadow: "0 4px 6px rgba(0, 0, 0, 0.1)",
    transition: "background-color 0.3s ease",
  },
  arrow: {
    fontWeight: "bold",
    fontSize: "rem",
  },
};

// Keyframes for cursor blinking
const cursorBlink = `
  @keyframes blink {
    0% { opacity: 1; }
    50% { opacity: 0; }
    100% { opacity: 1; }
  }
`;

document.styleSheets[0].insertRule(cursorBlink, document.styleSheets[0].cssRules.length);

export default WelcomePage;
